import React, { Component } from 'react';

export default class LeftOptionsNav extends Component {

    constructor(props) {
        super(props);
        this.state = {
            visible : false
        }
    }

    openModal() {
        this.setState({
            visible : true
        });
    }

    render() {
        return (
            <ul className="left_options" onClick={() => this.openModal()}>
                <li visible={this.state.visible} onClickAway={() => this.closeModal()} className="left_option show_shape">shape</li> 
                <li visible={this.state.visible} onClickAway={() => this.closeModal()}className="left_option show_face">face</li>
                <li visible={this.state.visible} onClickAway={() => this.closeModal()}className="left_option show_hair">hair</li>
                <li visible={this.state.visible} onClickAway={() => this.closeModal()}className="left_option show_accessories">accessories</li>
            </ul>
        );
    }
};
