import React from 'react'
import RightElementShape from './RightElementShape'
import RightElementFace from './RightElementFace'
import RightElementHair from './RightElementHair'
import RightElementAccessories from './RightElementAccessories'
var createReactClass = require('create-react-class');

let BlockRight = createReactClass({
    getDefaultProps() {
        return {
            data: [
                { title: 'shape', content: <RightElementShape /> },
                { title: 'face', content: <RightElementFace /> },
                { title: 'hair', content: <RightElementHair /> },
                { title: 'accessories', content: <RightElementAccessories /> }
            ]
        }
    },

    getInitialState() {
        return {
        current: 0
        }
    },

    handleClick(i) {
        this.setState({
            current: i
        });
    },

    render() {
        var list = this.props.data.map(function(d, i) {
            return <li className={'left_option' + (i === this.state.current ? ' active' : '')} key={'left_option'+i} onClick={this.handleClick.bind(this, i)}>{d.title}</li>  
        }.bind(this));

        return (
            <div className="mood_block-right">
                 <div className="right_elements">
                    <ul className="left_options">{list}</ul>
                    {this.props.data[this.state.current].content}
                </div>
            </div>
        );
    }
});


