import React from 'react'

const LeftOptionsNav = () => {

    function test() {
        var tabOptions = ["shape", "face", "hair", "accessories"];
        document.getElementsByClassName('left_option').onclick(function(){
            for(var i = 0; i < tabOptions.length; i++) {
                if("show_" + tabOptions[i]) {
                    document.getElementById("show_" + tabOptions[i]).style.display = 'block';
                }
                else{
                    document.getElementById("show_" + tabOptions[i]).style.display = 'none';
                }
            };
        });       
    }

    return (
        <ul className="left_options" onClick={() => test()}>
            <li className="left_option show_shape">shape</li> 
            <li className="left_option show_face">face</li>
            <li className="left_option show_hair">hair</li>
            <li className="left_option show_accessories">accessories</li>
        </ul>
    )
}

export default LeftOptionsNav
