import React from 'react';

const LeftOptionsNav = () => {

    document.getElementsByClassName("left_option").onclick = function(){
        let tabOptions = ["shape", "face", "hair", "accessories"];
        for(let i = 0; i < tabOptions.length; i++) {
            let eleClass = document.getElementsByClassName("left_option").className === "show_" + tabOptions[i];
            let eleId = document.getElementById("show_" + tabOptions[i]);
            console.log(eleClass)
            console.log(eleId)
            if(eleClass === eleId) {
                eleId.style.display = eleId.style.display = "block";
            } else {
                eleId.style.display = eleId.style.display = "none";
            }
        };     
    };

        // let ele = document.getElementById("show_shape");
        // console.log(ele)

        // ele.style.display = ele.style.display === "block" ? "none" : "block";
        // console.log(ele.style.display)

    return (
        <ul className="left_options">
            <li className="left_option show_shape">shape</li> 
            <li className="left_option show_face">face</li>
            <li className="left_option show_hair">hair</li>
            <li className="left_option show_accessories">accessories</li>
        </ul>
    )
}

export default LeftOptionsNav
