import React from 'react';

const LeftOptionsNav = () => {

    // function showHideLeft() {
    //     let tabOptions = ["shape", "face", "hair", "accessories"];
    //     for(let i = 0; i < tabOptions.length; i++) {
    //         let eleClass = document.getElementsByClassName("show_" + tabOptions[i]);
    //         let eleId = document.getElementById("show_" + tabOptions[i]);
    //         console.log(eleClass)
    //         console.log(eleId)
    //         eleId.style.display = eleId = eleClass ? "block" : "none";
    //     };     

    //     onClick={() => showHideLeft()} 

    //  }

    // return (
    //     <ul className="left_options">
    //         <li className="left_option show_shape">shape</li> 
    //         <li className="left_option show_face">face</li>
    //         <li className="left_option show_hair">hair</li>
    //         <li className="left_option show_accessories">accessories</li>
    //     </ul>
    // )

    var Tabs = React.createClass({
        getDefaultProps() {
          return {
            data: [
              { title: 'tab-1', content: 'content-1' },
              { title: 'tab-2', content: 'content-2' },
              { title: 'tab-3', content: 'content-3' },
              { title: 'tab-4', content: 'content-4' }
            ]
          }
        },

        getInitialState() {
          return {
            current: 0
          }
        },

        handleClick(i) {
            this.setState({
              current: i
            });
        },

        render() {
          var list = this.props.data.map(function(d, i) {
            return <li className={'tab' + (i === this.state.current? ' active' : '')} key={'tab-'+i} onClick={this.handleClick.bind(this, i)}>{d.title}</li>  
          }.bind(this));

          return (
            <div>
              <ul>{list}</ul>
              <div className='content'>
                  {this.props.data[this.state.current].content}
              </div>
            </div>
          );
        }
    });
}

export default LeftOptionsNav
