    // )

    let LeftOptionsNav = createReactClass({
        getDefaultProps() {
          return {
            data: [
              { title: 'shape', content: <LeftChoiceShape /> },
              { title: 'face', content: <LeftChoiceFace /> },
              { title: 'hair', content: <LeftChoiceHair /> },
              { title: 'accessories', content: <LeftChoiceAccessories /> }
            ]
          }
        },

        getInitialState() {
          return {
            current: 0
          }
        },

        handleClick(i) {
            this.setState({
              current: i
            });
        },

        render() {
          var list = this.props.data.map(function(d, i) {
            return <li className={'tab' + (i === this.state.current? ' active' : '')} key={'tab-'+i} onClick={this.handleClick.bind(this, i)}>{d.title}</li>  
          }.bind(this));

          return (
            <div>
              <ul className="left_options">{list}</ul>
              <div className="left_option">
                  {this.props.data[this.state.current].content}
              </div>
            </div>
          );
        }
    });
