import React, { Component } from 'react';
import Modal from 'react-awesome-modal';

export default class ButtonSave extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : false
        }
    }

    openModal() {
        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }

    render() {
        return (
            <section>
                <div class="mood_button-save">
                    <button class="button_save" onClick={() => this.openModal()}>Save my avatar</button>
                </div>
                <Modal 
                    visible={this.state.visible}
                    width="800"
                    height="400"
                    effect="fadeInUp"
                    onClickAway={() => this.closeModal()}
                >
                    <div>
                        <h1>Title</h1>
                        <p>Some Contents</p>
                        <a href="" onClick={() => this.closeModal()}>Close</a>
                    </div>
                </Modal>
            </section>
        );
    }
};
