class LeftOptionsNav extends React.Component {
    constructor(){
        super();

        this.state = {
           show: true
        }
    }

    changeColor(){
       this.setState({show: !this.state.black})
    }

    render(){
        let showHide = this.state.show ? "elementShow" : "elementHide";

        return (
            <ul className="left_options">
                <li className={showHide} onClick={this.changeColor.bind(this)} className="left_option show_shape">shape</li> 
                <li className={showHide} onClick={this.changeColor.bind(this)} className="left_option show_face">face</li>
                <li className={showHide} onClick={this.changeColor.bind(this)} className="left_option show_hair">hair</li>
                <li className={showHide} onClick={this.changeColor.bind(this)} className="left_option show_accessories">accessories</li>
            </ul>
        )
    }
}
