import React from 'react';

const LeftOptionsNav = () => {
    function showHideLeft() {
        let tabOptions = ["shape", "face", "hair", "accessories"];
        for(let i = 0; i < tabOptions.length; i++) {
            let eleClass = document.getElementsByClassName("show_" + tabOptions[i]);
            let eleId = document.getElementById("show_" + tabOptions[i]);
            console.log(eleClass)
            console.log(eleId)
            eleId.style.display = eleId = eleClass ? "block" : "none";
        };     
    }

    return (
        <ul className="left_options">
            <li onClick={() => showHideLeft()} className="left_option show_shape">shape</li> 
            <li onClick={() => showHideLeft()} className="left_option show_face">face</li>
            <li onClick={() => showHideLeft()} className="left_option show_hair">hair</li>
            <li onClick={() => showHideLeft()} className="left_option show_accessories">accessories</li>
        </ul>
    )
}

export default LeftOptionsNav


class Test extends React.Component {
    constructor(){
        super();

        this.state = {
           black: true
        }
    }

    changeColor(){
       this.setState({black: !this.state.black})
    }

    render(){
        let btn_class = this.state.black ? "blackButton" : "whiteButton";

        return (
             <button className={btn_class} onClick={this.changeColor.bind(this)}>
                  Button
             </button>
        )
    }
}

React.render(<Test />, document.getElementById('container'));
