class LeftOptionsNav extends React.Component {
    constructor(){
        super();

        this.state = {
           show: true
        }
    }

    changeColor(){
       this.setState({show: !this.state.black})
    }

    render(){

        return (
            <ul className="left_options">
                <li onClick={this.changeColor.bind(this)} className="left_option show_shape">shape</li> 
                <li onClick={this.changeColor.bind(this)} className="left_option show_face">face</li>
                <li onClick={this.changeColor.bind(this)} className="left_option show_hair">hair</li>
                <li onClick={this.changeColor.bind(this)} className="left_option show_accessories">accessories</li>
            </ul>
        )
    }
}
